import React from "react";
import "./Login.css"; // Certifique-se de que este arquivo exista
// import logoWhite from "../assets/logoWhite.svg";
import button from "../assets/button.svg";



const Login = () => {
  const preventDrag = (e) => e.preventDefault();
  document.title = "Login";

  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [mensagem, setMensagem] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });

    if (response.ok) {
      // Login OK: provavelmente retorna o token
      const token = await response.text(); // ou response.json() se retornar JSON
      console.log('Token recebido:', token);
      return token; // você pode salvar no estado, localStorage, etc
    } else if (response.status === 401) {
      // Login falhou
      const errorMsg = await response.text();
      console.error('Erro de login:', errorMsg);
      throw new Error(errorMsg);
    } else {
      // Outro erro inesperado
      throw new Error('Erro inesperado no login');
    }
    } catch (error) {
      console.error('Falha ao conectar com o backend:', error);
      throw error;
    }
  };

  return (
    <div className="container">
      <div className="cell i1">
        <svg
          id="logo"
          className="logo"
          viewBox="0 0 516 976"
          xmlnsSvg="http://www.w3.org/2000/svg"
          onDragStart={preventDrag}
        >
        </svg>
      </div>
      <div className="cell i2">
        {mensagem && <span style={{ color: "red" }}>{mensagem}</span>}
        <form onSubmit={handleSubmit}>
          <label>
            <input type="email" placeholder="Email" id="email" name="email" value={FormData.email} onChange={handleChange} required />
          </label>
          <br />
          <label>
            <input type="password" placeholder="Senha" id="password" name="password" value={FormData.password} onChange={handleChange} required />
          </label>
          <br />
          <label>
            <input type="submit" style={{ display: "none" }} />
            <svg
              id="button1"
              className="button"
              viewBox="0 0 251 44"
              xmlnssvg="http://www.w3.org/2000/svg"
              onDragStart={preventDrag}
            >
              <image href={button}/>
              <text
                x="50%"
                y="50%"
                dominantBaseline="middle"
                textAnchor="middle"
                className="buttonText"
              >
                Cadastrar
              </text>
            </svg>
          </label>
        </form>
      </div>
    </div>
  );
};

export default Login;
