import React, { useEffect, useState } from 'react';

const types = [
  "AMMUNITION", "ARMOR", "BOOKS", "CLOTHING", "FOOD",
  "INGREDIENTS", "MISCELLANEOUS", "ORES", "POTIONS",
  "SOUL GEMS", "WEAPONS"
];

export default function Sidebar() {
  const [index, setIndex] = useState(0);
  const [visibleTypes, setVisibleTypes] = useState([]);

  useEffect(() => {
    const updateSidebar = () => {
      const height = window.innerHeight;
      let qtd = height >= 980 ? 9 : height >= 760 ? 7 : height >= 640 ? 5 : 3;
      let lim = Math.floor(qtd / 2);
      const list = [];
      for (let j = index - lim; j <= index + lim; j++) {
        let id = j % types.length;
        if (id < 0) id += types.length;
        list.push({ name: types[id], class: getClass(j - index) });
      }
      setVisibleTypes(list);
    };

    const getClass = (diff) => {
      switch (Math.abs(diff)) {
        case 4: return 'sideBarItem edge3';
        case 3: return 'sideBarItem edge2';
        case 2: return 'sideBarItem edge1';
        case 1: return 'sideBarItem center';
        default: return 'sideBarItem centerSelected';
      }
    };

    updateSidebar();
    window.addEventListener('resize', updateSidebar);
    return () => window.removeEventListener('resize', updateSidebar);
  }, [index]);

  return (
    <div className="sidebar">
      <div>
        <img className="logo" src="assets/gold.png" alt="Logo" />
      </div>
      <div className="productClasses">
        <ul id="classes-ul">
          {visibleTypes.map((item, i) => (
            <li key={i} className={item.class}>
              <a href="#">{item.name}</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
